@extends('layouts.mb-app')

@section('title', 'Kalkulator Zakat')
@section('css')
    <style>
        .menu {
            background: #F8F8F8;
            border-radius: 12px;
        }

        .menu:hover,
        .menu.active {
            background: #A6E0BE;
            cursor: pointer;
        }

        .menu-wrap span {
            margin: 0 auto;
            margin-bottom: 12px;
            color: #7F8E9D;
            font-size: small;
        }
        .menu-wrap{
            padding-top: 80px;
        }

        .content {
            padding-top: 80px;
            padding-bottom: 24px;
            height: 100vh;
            border-left: 1px solid #E9ECF4;
            overflow: scroll;
        }

        #select-period {
            margin-top: 12px;
            border: none;
            width: 100%;
            background: #F2F2F2;
        }

        #select-period .nav-item {
            flex: 1;
            text-align: center;
        }

        #select-period .nav-link {
            background: #F2F2F2;
            border: none;
            color: var(--primary-color);
            border-bottom: 6px solid transparent;
        }

        #select-period .nav-link.active {
            border-bottom: 6px solid var(--primary-color);
        }

        .text-theme {
            color: var(--primary-color)
        }

        .bg-theme {
            background: var(--primary-color)
        }
        #nav-top-secondary {
            position: fixed;
        }

    </style>
@endsection

@section('content')
