        let zakat = parseInt(income || 0) + parseInt(oincome || 0) - parseInt(spending || 0)
        let _gold = 0
        let _x = ""
       if(p=="monthly"){
        _zakat = zakat * 2.5 / 100
        _gold = ((gold * 85)/12)
       }else{
        _zakat = zakat * 2.5 / 100
        _gold = ((gold * 85))
       }
        if(zakat < _gold) {
            $("#"+p+"-penghasilan-no").removeClass("d-none")
            $("#"+p+"-penghasilan-yes").addClass("d-none")
            $("#penghasilan-button-"+p).html("Infak sekarang")
            _x = btoa(JSON.stringify({
                id:"0",
                nominal:"0",
                referral_code: getReferralCode(),
                type:"sedekah",
                defaults:_zakat,
                strict:false
            }))
        } else {
            $("#"+p+"-penghasilan-no").addClass("d-none")
            $("#"+p+"-penghasilan-yes").removeClass("d-none")
            $("#penghasilan-button-"+p).html("Tunaikan Zakat")
            _x = btoa(JSON.stringify({
                id:"0",
                nominal:"0",
                referral_code: getReferralCode(),
                type:"zakat",
                defaults:_zakat,
                strict:true
            }))
        }

       if(_zakat > 0){
            _zakat = formatRupiah(Math.round(_zakat).toString(), "Rp")
            $("#"+p+"-total").html(_zakat)
            $("#penghasilan-button-"+p).click(function(){
                window.location.href = "{{url('nominal')}}/"+_x
            })
       }else{
            $("#"+p+"-total").html("Rp0")
            $("#penghasilan-button-"+p).click(function(){
                window.location.href = ""
            })
       }
    }
